import React from 'react';
import socketIOClient from 'socket.io-client';
const ENDPOINT = "http://localhost:3001"; ;

function Chat({ username }) {

    const socket = socketIOClient(ENDPOINT);
    socket.emit('new-user', username);
    let chatHistory = [];

    socket.on('chat-message', data => {
        chatHistory.push(<div>{data.username}: {data.message}</div>);
    });

    socket.on('user-connected', username => {
        chatHistory.push(<div>{username} connected</div>);
    });

    return (
        <div>
            <div id="message-container">{chatHistory}</div>
            <form id="send-container" onSubmit={(event) => handleSubmit(event)}>
                <input type="text" id="message-input"></input>
                <button type="submit" id="send-button">Send</button>
            </form>
        </div>
    )
}

handleSubmit = (event) => {
    messageInput = document.getElementById('message-input');
    event.preventDefault();
    const message = messageInput.value;
    chatHistory.push(<div>{username}: {message}</div>);
    socket.emit('send-chat-message', message);
    messageInput.value = '';
}

myMessage = (message) => {
    chatHistory.push(<div>{message}</div>);
}

export default Chat;
