import React from 'react';
import socketIOClient from 'socket.io-client';
const socket = socketIOClient(ENDPOINT);
const ENDPOINT = "http://localhost:3001";
let chatHistory = [];

class Chat extends React.Component {

    constructor(props)
    {
        super(props)
        this.state = {
            username: props.username
        }
    }

    componentDidMount() {
        socket.emit('new-user', this.state.username);

        socket.on('chat-message', data => {
            chatHistory.push(<div>{data.username}: {data.message}</div>);
        });

        socket.on('user-connected', username=> {
            chatHistory.push(<div>{username} connected</div>);
        });
    }

    handleSubmit = (event) => {
        event.preventDefault();
        const message = document.getElementById('message-input').value;
        chatHistory.push(<div>{this.state.username}: {message}</div>);
        socket.emit('send-chat-message', message);
        document.getElementById('message-input').value = '';
    }


    render() {
        return (
            <div>
                <div id="message-container">{chatHistory}</div>
                <form id="send-container" onSubmit={(event) => this.handleSubmit(event)}>
                    <input type="text" id="message-input"></input>
                    <button type="submit" id="send-button">Send</button>
                </form>
            </div>
        )
    }


}

export default Chat;
