import React from 'react';
import socketIOClient from 'socket.io-client';
const ENDPOINT = "http://localhost:3001"; ;
const socket = socketIOClient(ENDPOINT);

class Chat extends React.Component {

    constructor(props)
    {
        super(props)
        this.state = {
            messageHistory: []
        }
    }

    componentDidMount() {
        socket.emit('new-user', this.props.username);

        socket.on('chat-message', data => {
            let chatHistory = this.state.messageHistory;
            chatHistory.push(<div>{data.username}: {data.message}</div>);
            this.setState({ messageHistory: chatHistory });
        });

        socket.on('user-connected', username => {
            let chatHistory = this.state.messageHistory;
            chatHistory.push(<div>{username} connected</div>);
            this.setState({ messageHistory: chatHistory });
        });
    }

    handleSubmit = (e) => {
        e.preventDefault();
        let message = document.getElementById('message-input').value;

        let chatHistory = this.state.messageHistory;
        chatHistory.push(<div>{this.props.username}: {message}</div>);

        console.log(chatHistory);   // testing

        this.setState({ messageHistory: chatHistory });

        socket.emit('send-chat-message', message)
    }

    render()
    {
        return (
            <div>
                <div id="message-container">{this.idchatHistory}</div>
                <form id="send-container" onSubmit={(e) => this.handleSubmit(e)}>
                    <input type="text" id="message-input"></input>
                    <button type="submit" id="send-button">Send</button>
                </form>
            </div>
        )
    }

}

export default Chat;
