import React from 'react'
import socketIOClient from 'socket.io-client';
const ENDPOINT = "http://24.57.124.99:3001";    // this is just my local ip address, socket uses 3001 port on my app
const socket = socketIOClient(ENDPOINT);

class SideBar extends React.Component{

    constructor(props)
    {
        super(props);
        this.state = {
            users: []
        }
    }

    componentDidMount() 
    {
        socket.on('new-user', (usernames) => {
            usernames ? this.setState({ users: usernames }) : this.setState({ users: [] });
        })
    }

    render() 
    {
        return (
            <div>
                {this.state.users}
            </div>
        )
    }

}

export default SideBar;
